var logStatus = true;
var logError = true;
var logDebug = true;
var logWarn = true;

module.exports = (function() {
	var self = this;
	
	var openRed = "\033[31m";
	var openGreen = "\033[32m";
	var openYellow = "\033[33m";
	var openCyan = "\033[36m"
	
	var closeColor = "\033[0m"
	
	self.status = function(origin, message, indentLevel) {
		var prefix = openGreen;
		if (indentLevel) {
			while(indentLevel > 0) {
				prefix += "\t";
				indentLevel--;
			}
		}
		
		if (logStatus) {
			console.log(prefix + "[" + origin + "] " + message + closeColor);
		}
	}
	
	self.error = function(origin, message, indentLevel) {
		var prefix = openRed;
		if (indentLevel) {
			while(indentLevel > 0) {
				prefix += "\t";
				indentLevel--;
			}
		}
		
		if (logError) {
			console.log(prefix + "[" + origin + "] " + message + closeColor);
		}
	}
	
	self.debug = function(origin, message, indentLevel) {
		var prefix = openCyan;
		if (indentLevel) {
			while(indentLevel > 0) {
				prefix += "\t";
				indentLevel--;
			}
		}
		
		if (logDebug) {
			console.log(prefix + "[" + origin + "] " + message + closeColor)
		}
	}
	
	self.warn = function(origin, message, indentLevel) {
		var prefix = openYellow;
		if (indentLevel) {
			while(indentLevel > 0) {
				prefix += "\t";
				indentLevel--;
			}
		}
		
		if (logWarn) {
			console.log(prefix + "[" + origin + "] " + message + closeColor);
		}
	}
	
	return self;
})();